  //       (error) => {
  //         setError(error);
  //       }
  //     );
  // }, [resourceType]);

  useEffect(() => {
    fetch("https://restcountries.com/v3.1/all").then((res) =>
      res.json().then((result) => {
        console.log(result);
        return setCountryList(result);
      })
    );
  }, []);

  return (
    <div className="bg-slate-200">
      <h2 className="text-2xl font-bold">Country data</h2>

      <h3>Filter Countries</h3>
      <ul className="flex justify-between w-[50%] mx-auto">
        <li>
          {" "}
          <button
            className=" w-20 bg-blue-700 px-2 py-3"
            onClick={() => setSearchTerm("Africa")}
          >
            Africa
          </button>
        </li>
        <li>
          {" "}
          <button
            className=" w-20 bg-blue-700 px-2 py-3"
            onClick={() => setSearchTerm("Asia")}
          >
            Asia
          </button>
        </li>
        <li>
          {" "}
          <button
            className=" w-20 bg-blue-700 px-2 py-3"
            onClick={() => setSearchTerm("North America")}
          >
            America
          </button>
        </li>
        <li>
          {" "}
          <button
            className=" w-20 bg-blue-700 px-2 py-3"
            onClick={() => setSearchTerm("Europe")}
          >
            Europe
          </button>
        </li>
      </ul>

      <div className="grid grid-cols-4 gap-2">
        {countryList
          .filter((country) => {
            return searchTerm.toLowerCase() === ""
              ? country
              : country.continents.includes(searchTerm);
          })
          .map((country) => {
            return (
              <div
                key={country.population}
                className="card w-[200px] bg-blue-300 shadow-xl p-3"
              >
                <figure>
                  <img src={country.flags.png} alt="Shoes" />
                </figure>
                <div className="card-body">
                  <h2 className="card-title">{country.name.common}</h2>
                  <p>{country.continents}</p>
                  <p>{country.capital}</p>
                </div>
              </div>
            );
          })}
      </div>
    </div>

    // <div>
    //   <header>
    //     <h1>{resourceType}</h1>
    //     <div>
    //       <button onClick={() => setResourceType("users")}>Users</button>
    //       <button onClick={() => setResourceType("posts")}>Posts</button>
