    //           : item.name.toLowerCase().includes(q);
    //       })
    //       .map((item) => (
    //         <div key={item.id}>
    //           <h2>{item.name}</h2>
    //           <p>@{item.username}</p>
    //           <p>{item.phone}</p>
    //         </div>
    //       ))}
    //   </header>
    // </div>
  );
}

export default App;
