import React from "react";
import { Link, useParams } from "react-router-dom";

export default function CountryInfo({ countryData }) {
  let { countryId } = useParams();
  return (
    <div>
      <div>
        <img
          src={countryData.flags.png}
          alt={`${countryData.name.common}'s official flag`}
        />
        <h2>Common name: {countryData.name.common}</h2>
        <h2>Official name: {countryData.name.official}</h2>

        <div>
          <p>Population: {countryData.population}</p>
          <p>Area: {countryData.area}</p>
          <p>Timezone: {countryData.timezones}</p>
        </div>
      </div>
      <Link to="/" className="text-2xl text-orange-600 block">
        Back to homepage
      </Link>
    </div>
  );
}
