import { useState, useEffect } from "react";
import { BrowserRouter, Routes, Route, Link } from "react-router-dom";

import "./App.css";
import CountryInfo from "./components/CountryInfo";
import CountryList from "./components/CountryList";

function App() {
  const [countryList, setCountryList] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const [error, setError] = useState(null);
  const [getCountryName, setGetCountryName] = useState("");

  useEffect(() => {
    axios
      .get("https://restcountries.com/v3.1/all")
      .then((response) => {
        setCountryList(response.data);
      })
      .catch((error) => {
        console.log(error.message);
        setError(error);
      });
  }, []);

  // Filter results based on continent
  const filterFunction = countryList.filter((country) => {
    return searchTerm.toLowerCase() === ""
      ? country
      : country.continents.includes(searchTerm);
  });

  const africabtn = document.querySelector(".africa");

  return (
    <BrowserRouter>
      <Routes>
        <Route
          path="/"
          element={
            <div className="bg-slate-200">
              <h2 className="text-2xl font-bold">Country data</h2>

              <h3>Filter Countries</h3>
              <ul className="grid grid-cols-5 w-[50%] mx-auto">
                <li>
                  {" "}
                  <button
                    className=" w-20 bg-blue-700 px-2 py-3"
                    onClick={() => {
                      setSearchTerm("Africa");
                      africabtn.classList.toggle("hidden");
                    }}
                  >
                    Africa
                  </button>
                </li>
                <li>
                  {" "}
                  <button
                    className=" w-20 bg-blue-700 px-2 py-3"
                    onClick={() => setSearchTerm("Asia")}
                  >
                    Asia
                  </button>
                </li>
                <li>
                  {" "}
                  <button
                    className=" w-20 bg-blue-700 px-2 py-3"
                    onClick={() => setSearchTerm("North America")}
                  >
                    America
                  </button>
                </li>
                <li>
                  {" "}
                  <button
                    className=" w-20 bg-blue-700 px-2 py-3"
                    onClick={() => setSearchTerm("Europe")}
                  >
                    Europe
                  </button>
                </li>
              </ul>

              <div>
                <ul className="flex gap-4 py-2">
                  <li className="hidden africa">Africa</li>
                  <li className="hidden asia">Asia</li>
                  <li className="hidden america">aMERICA</li>
                </ul>
              </div>

              {error ? (
                <div className="flex">
                  There's was an issue while loading your request
                </div>
              ) : (
                <div className="grid grid-cols-5 gap-2">
                  {filterFunction.map((country, i) => {
                    return (
                      <div
                        key={i}
                        className="card w-[200px] bg-blue-300 shadow-xl p-3"
                        onClick={() => {
                          setGetCountryName(country.name.common);
                          // handleCountryDetails();
                        }}
                      >
                        <figure>
                          <img src={country.flags.png} alt="Shoes" />
                        </figure>
                        <div className="card-body">
                          <h2 className="card-title">{country.name.common}</h2>
                          <p>{country.continents}</p>
                          <p>{country.capital}</p>
                        </div>
                        <Link
                          to={`/country/${i}`}
                          className="bg-blue-700 px-2 py-2 text-white"
                        >
                          View More Details
                        </Link>
                      </div>
                    );
                  })}
                </div>
              )}
            </div>
          }
        />
        <Route
          path="/country/:countryId"
          element={<CountryInfo countryName={getCountryName} />}
        />
      </Routes>
    </BrowserRouter>
  );
}

export default App;
