import { useState, useEffect } from "react";
import axios from "axios";
import "./App.css";

function App() {
  // const [resourceType, setResourceType] = useState("users");
  // const [items, setItems] = useState([]);
  // const [error, setError] = useState(null);
  // const [q, setQ] = useState("");
  // const [searchParam] = useState(["username", "name"]);

  const [countryList, setCountryList] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const [error, setError] = useState(null);

  useEffect(() => {
    axios
      .get("https://restcountries.com/v3.1/alls")
      .then((response) => setCountryList(response.data))
      .catch((error) => console.log(error.message));
  }, []);

  // Filter results based on continent
  const filterFunction = countryList.filter((country) => {
    return searchTerm.toLowerCase() === ""
      ? country
      : country.continents.includes(searchTerm);
  });

  const africabtn = document.querySelector(".africa");

  return (
    <div className="bg-slate-200">
      <h2 className="text-2xl font-bold">Country data</h2>

      <h3>Filter Countries</h3>
      <ul className="grid grid-cols-5 w-[50%] mx-auto">
        <li>
          {" "}
          <button
            className=" w-20 bg-blue-700 px-2 py-3"
            onClick={() => {
              setSearchTerm("Africa");
              africabtn.classList.toggle("hidden");
            }}
          >
            Africa
          </button>
        </li>
        <li>
          {" "}
          <button
            className=" w-20 bg-blue-700 px-2 py-3"
            onClick={() => setSearchTerm("Asia")}
          >
            Asia
          </button>
        </li>
        <li>
          {" "}
          <button
            className=" w-20 bg-blue-700 px-2 py-3"
            onClick={() => setSearchTerm("North America")}
          >
            America
          </button>
        </li>
        <li>
          {" "}
          <button
            className=" w-20 bg-blue-700 px-2 py-3"
            onClick={() => setSearchTerm("Europe")}
          >
            Europe
          </button>
        </li>
      </ul>

      <div>
        <ul className="flex gap-4 py-2">
          <li className="hidden africa">Africa</li>
          <li className="hidden asia">Asia</li>
          <li className="hidden america">aMERICA</li>
        </ul>
      </div>

      <div className="grid grid-cols-5 gap-2">
        {filterFunction.map((country, i) => {
          return (
            <div key={i} className="card w-[200px] bg-blue-300 shadow-xl p-3">
              <figure>
                <img src={country.flags.png} alt="Shoes" />
              </figure>
              <div className="card-body">
                <h2 className="card-title">{country.name.common}</h2>
                <p>{country.continents}</p>
                <p>{country.capital}</p>
              </div>
            </div>
          );
        })}
      </div>
    </div>

    // <div>
    //   <header>
    //     <h1>{resourceType}</h1>
    //     <div>
    //       <button onClick={() => setResourceType("users")}>Users</button>
    //       <button onClick={() => setResourceType("posts")}>Posts</button>
    //       <button onClick={() => setResourceType("comments")}>Comments</button>
    //     </div>
    //     <div></div>
    //     <div className="search-wrapper">
    //       <label htmlFor="search-form">
    //         <input
    //           type="search"
    //           name="search-form"
    //           id="search-form"
    //           className="search-input"
    //           placeholder="Search for..."
    //           value={q}
    //           /*
    //                             // set the value of our useState q
    //                             //  anytime the user types in the search box
    //                             */
    //           onChange={(e) => setQ(e.target.value)}
    //         />
    //         <span className="sr-only">Search countries here</span>
    //       </label>
    //     </div>
    //     {items
    //       .filter((item) => {
    //         return q.toLowerCase() === ""
    //           ? item
    //           : item.name.toLowerCase().includes(q);
    //       })
    //       .map((item) => (
    //         <div key={item.id}>
    //           <h2>{item.name}</h2>
    //           <p>@{item.username}</p>
    //           <p>{item.phone}</p>
    //         </div>
    //       ))}
    //   </header>
    // </div>
  );
}

export default App;
